import React from 'react';
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Box,
  Chip
} from '@mui/material';

const ProductCard = ({ product, onClick }) => {
  return (
    <Card
      sx={{
        height: 280,
        cursor: 'pointer',
        backgroundColor: '#424242',
        color: '#ffffff',
        transition: 'transform 0.2s, box-shadow 0.2s',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 8px 16px rgba(0, 0, 0, 0.4)'
        }
      }}
      onClick={onClick}
    >
      <CardMedia
        component="img"
        height="160"
        image={product.imagePath}
        alt={product.name}
        sx={{
          objectFit: 'cover'
        }}
        onError={(e) => {
          e.target.src = 'https://via.placeholder.com/300x160?text=No+Image';
        }}
      />
      <CardContent sx={{ p: 2 }}>
        <Typography
          variant="subtitle1"
          component="h3"
          sx={{
            fontWeight: 600,
            fontSize: '0.9rem',
            mb: 1,
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            whiteSpace: 'nowrap',
            color: '#ffffff'
          }}
        >
          {product.name}
        </Typography>

        <Typography
          variant="h6"
          sx={{
            fontWeight: 'bold',
            fontSize: '1rem',
            mb: 1,
            color: '#FFC107'
          }}
        >
          {product.price}
        </Typography>

        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography
            variant="caption"
            sx={{ 
              fontSize: '0.75rem',
              color: '#b0b0b0'
            }}
          >
            Sold: {product.soldCount}
          </Typography>
          <Chip 
            label="Available" 
            size="small" 
            sx={{ 
              fontSize: '0.7rem',
              backgroundColor: '#4caf50',
              color: '#ffffff',
              border: 'none'
            }} 
          />
        </Box>
      </CardContent>
    </Card>
  );
};

export default ProductCard;
