import React from 'react';
import { Chip } from '@mui/material';
import {
  AllInclusive,
  Build,
  Power,
  Lightbulb,
  ElectricBolt,
  Cable,
  Category
} from '@mui/icons-material';

const CategoryButton = ({ label, icon, isSelected, onPressed }) => {
  const getIconComponent = (iconName) => {
    switch (iconName) {
      case 'all_inclusive':
        return <AllInclusive />;
      case 'build':
        return <Build />;
      case 'power':
        return <Power />;
      case 'lightbulb':
        return <Lightbulb />;
      case 'electric_bolt':
        return <ElectricBolt />;
      case 'cable':
        return <Cable />;
      default:
        return <Category />;
    }
  };

  return (
    <Chip
      icon={icon ? getIconComponent(icon) : undefined}
      label={label}
      onClick={onPressed}
      sx={{
        minWidth: 'fit-content',
        whiteSpace: 'nowrap',
        borderRadius: '25px',
        padding: '12px 16px',
        fontSize: '13px',
        fontWeight: isSelected ? 600 : 500,
        backgroundColor: isSelected ? '#d32f2f' : '#424242',
        color: isSelected ? '#ffffff' : '#b0b0b0',
        border: isSelected ? 'none' : '1px solid #616161',
        boxShadow: isSelected ? '0 4px 8px rgba(211, 47, 47, 0.3)' : 'none',
        '&:hover': {
          backgroundColor: isSelected ? '#b71c1c' : '#525252',
          color: '#ffffff'
        },
        '& .MuiChip-icon': {
          fontSize: '18px',
          color: isSelected ? '#ffffff' : '#b0b0b0'
        }
      }}
    />
  );
};

export default CategoryButton;
